mapper.select:
SELECT 

FROM t_article_cat a

WHERE a.id = #{id}

SELECT 

FROM t_article_cat a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM t_article_cat a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE t_article_cat SET 	
	name = #{name},
	sort = #{sort},
	parent_id = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE t_article_cat SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
