mapper.select:
SELECT 

FROM t_article a

WHERE a.id = #{id}

SELECT 

FROM t_article a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.cat_id = #{cat.id}


		AND a.cat_name = #{catName}


		AND a.article_title LIKE 
			'%'||#{articleTitle}||'%'
			'%'+#{articleTitle}+'%'
			concat('%',#{articleTitle},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_article a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_article SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
